mapper.select:
select b.id,
       b.parent_id,
       b.domain_id,
       b.str_flag,
       b.resources_type,
       b.enabled,
       a.class_name,
       a.method_name,
       a.method_params,
       a.api_path,
       a.api_exist
from api_permission a left join permission b on (a.permission_id = b.id)
where a.class_name = #{className}
  and a.method_name = #{methodName}
  and a.method_params = #{methodParams}
  and b.domain_id = #{domainId}

select * from api_permission


        and class_name like concat('%', #{query.className}, '%')


        and method_name like concat('%', #{query.methodName}, '%')


        and method_params like concat('%', #{query.methodParams}, '%')


        and api_path like concat('%', #{query.apiPath}, '%')


        and api_exist = #{query.apiExist}


        and create_at>=#{query.createAtStart}


         and create_at<=#{query.createAtEnd}
