mapper.select:
select a.id AS permission_id, a.sys_name, a.title, a.permission_str, a.resources_type, a.description, a.create_at, a.update_at,
b.id AS web_permission_id, b.need_authorization, b.target_class, b.target_method, b.target_method_params, b.resources_url, b.target_exist from permission a left join web_permission b on (a.permission_str=b.permission_str)
and a.sys_name=#{query.sysName}
and a.title like concat('%', #{query.title}, '%')
and a.permission_str like concat('%', #{query.permissionStr}, '%')
and a.resources_type=#{query.resourcesType}
and b.target_class like concat('%', #{query.targetClass}, '%')
and b.target_method like concat('%', #{query.targetMethod}, '%')
and b.resources_url like concat('%', #{query.resourcesUrl}, '%')
and b.need_authorization=#{query.needAuthorization}
and b.target_exist=#{query.targetExist}
order by a.id
