mapper.select:
select
    b.name
from user_role a left join role b on (a.role_id=b.id and a.domain_id=b.domain_id)
where b.enabled=1 and b.name is not null and a.domain_id=#{domainId} and a.uid=#{uid}

select a.*,b.name as domainName from role a
left join domain b on a.domain_id = b.id


        and a.id=#{query.id}


        and a.domain_id=#{query.domainId}


        and a.name like concat('%',#{query.name},'%')


        and a.enabled = #{query.enabled}
