mapper.select:
select
    a.*,
    b.name as domain_name
from scan_code_login a left join domain b on (a.domain_id=b.id)


        and a.domain_id=#{query.domainId}


        and a.scan_code=#{query.scanCode}


        and a.scan_code_state=#{query.scanCodeState}


        and a.bind_token_id=#{query.bindTokenId}


        and a.bind_token_time>=#{query.bindTokenTimeStart}


         and a.bind_token_time<=#{query.bindTokenTimeEnd} 


        and a.confirm_time>=#{query.confirmTimeStart}


         and a.confirm_time<=#{query.confirmTimeEnd} 


        and a.login_time>=#{query.loginTimeStart}


         and a.login_time<=#{query.loginTimeEnd} 


        and a.token_id=#{query.tokenId}


        and a.create_at>=#{query.createAtStart}


         and a.create_at<=#{query.createAtEnd}
