mapper.select:
select uid, login_name, telephone, email, expired_time, enabled, nickname, avatar, register_channel, from_source, description, create_at, update_at from user
and (login_name like concat('%', #{query.keyword}, '%') or telephone like concat('%', #{query.keyword}, '%') or email like concat('%', #{query.keyword}, '%') or nickname like concat('%', #{query.keyword}, '%'))
and create_at>=#{query.createAtStart}
and create_at<=#{query.createAtEnd}
