mapper.select:
select
a.*,
b.name as domainName,
c.nickname,
c.login_name as loginName,
c.telephone,
c.email
from user_register_log a
left join domain b on (a.register_domain_id = b.id)
left join user c on (a.register_uid = c.uid)


        and a.id=#{query.id}


        and a.register_domain_id=#{query.domainId}


        and a.register_ip like concat('%', #{query.registerIp}, '%')


        and a.register_channel = #{query.registerChannel}


        or a.register_type = #{query.registerType}


        or a.request_result = #{query.requestResult}


        and (c.login_name like concat('%', #{query.userKeyword}, '%')
        or c.telephone like concat('%', #{query.userKeyword}, '%')
        or c.email like concat('%', #{query.userKeyword}, '%')
        or c.nickname like concat('%', #{query.userKeyword}, '%'))


        and a.register_time>=#{query.registerTimeStart}


         and a.register_time<=#{query.registerTimeEnd}
